import { useState } from 'react'
import { useNavigate } from 'react-router-dom'

function NewParticipantForm() {

  const navigate = useNavigate()

  const [name, setName] = useState('')
  const [habits, setHabits] = useState('')

  const handleChangeName = e => setName(e.target.value)
  const handleChangeHabits = e => setHabits(e.target.value)

  function handleSubmit(e) {
    e.preventDefault()

    const OPTIONS = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ name, bad_habits: habits })
    }

    fetch('http://localhost:5000/participants', OPTIONS)
    .then( res => res.json() )
    .then( () => navigate('/registry') )
    // the only big difference here is we navigate back to registry
    // one nice side effect of this is navigate will load with our new participant since it fetches again!
  }

  return (

    <div className="new-participant-page">

      <form onSubmit={handleSubmit} className="participant-form">

        <h2>Register Your New Investigator</h2>

        <label htmlFor="name">Investigator Name:</label>
        <input type="text"
          name="name"
          onChange={handleChangeName}
          value={name}
          placeholder='write the name here'
        />

        <br/>

        <label htmlFor="habits">Investigator Bad Habits:</label>
        <input type="text"
          name="habits"
          onChange={handleChangeHabits}
          value={habits}
          placeholder="bad habits here"
        />

        <br/>

        <input
          type="submit"
          value="Register Private Eye"
        />

      </form>

      <img src="https://handsonpeople.com.au/wp-content/uploads/elementor/thumbs/shutterstock_20093125131-ppngpnrewsxhm3fii7u8gp5gex4cit7vho9bgj09ce.jpg"alt={"Building Community"} />

    </div>

  )
}

export default NewParticipantForm
